import React, {useState} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MuiCard from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import WordInput from './Input/Input';
import Languages from './Languages/Languages';
import WordType from './WordType/WordType';

const useStyles = makeStyles({
	root: {
		minWidth: 275,
		// maxWidth: "75%"
	},
	title: {
		fontSize: 14,
	},
	pos: {
		marginBottom: 4,
	},
	actions: {
		margin: "auto",
		textAlighn: "center",
		display: "flex",
		flexDirection: "row",
		justifyContent: "center"
	}
});

type NewCardProps = {
	label: string;
	placeholder: string;
	word?: boolean;
}

function NewCard(props: NewCardProps) {
	const classes = useStyles();
	const [word, setWord] = useState('');

	return (
		<MuiCard className={classes.root} variant={"outlined"}>
			<CardContent>
				<Typography variant="h6">{props.label}</Typography>
				<WordInput label={props.label} placeholder={props.placeholder} />
			</CardContent>
			<CardActions className={classes.actions}>
				<Languages />
				{props.word ? <WordType /> : null}
			</CardActions>
		</MuiCard>
	);
}

export default NewCard;
