import React from "react";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Typography from "@material-ui/core/Typography";
import styled from "styled-components";
import Card from "../Card/Card";
import Header from "../Header/Header";
import Misc from "../../hoc/Misc/Misc";
import Select from "../Select/Select";
import Input from "../Input/Input";
import "./Home.css";
import { makeStyles } from "@material-ui/core/styles";
import Flex from "../UI/Flex/Flex";
import NewCard from "../NewCard/NewCard";

const useStyles = makeStyles({
  container: {
    alignItems: "center",
    justifyContent: "center"
  }
});

const StyledButton = styled(Button)`
  margin: 1rem;
  background-color: #43a047;
  width: 10rem;

  :hover {
    background-color: #388e3c;
  }
`;

function Home() {
  const classes = useStyles();

  return (
    <Misc>
      <Header />
      <Container>
        <Flex direction="col">
          <Typography color="textSecondary" gutterBottom>
            Add new Card
          </Typography>
          <br />
          <Flex direction="row" align="center" justify="center">
            {/* <Input label="Word" placeholder="Write a new word..."/> */}
            {/* <Select /> */}
            <NewCard label="Word" placeholder="Write a new word..."></NewCard>
            <NewCard label="Translation" placeholder="Write the translation..." dir="left"></NewCard>
          </Flex>
          <Flex direction="row" align="center" justify="center">
            <StyledButton variant="contained" color="primary">Save</StyledButton>
          </Flex>
        </Flex>
      </Container>
      {/* <Card word={"همه کاره"} lang={"Persian"} type={"adj."}></Card> */}
    </Misc >
  );
}

export default Home;
