import React from 'react';
import styled from 'styled-components';

const Flex = styled.div`
	display: flex;
`;

interface ColProps {
	children: any;
	direction: "initial" | "inherit" | "row" | "row-reversed" | "col" | "col-reversed";
	align?: "center" | "stretch" | "baseline" | "start" | "end";
	justify?: "center" | "between" | "around" | "start" | "end";
}

const flex = (props: ColProps) => <Flex className={props.direction + props.align != "" ? "align-center" : "" + props.justify}>{props.children}</Flex>

export default flex;
