import React from "react";

import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import TextField from "@material-ui/core/TextField";
import styled from "styled-components";
import "react-bootstrap";
import { Row } from "reactstrap";
import Card from "../Card/Card";

import "./Home.css";

type MyProps = {
  message?: string;
};

const StyledButton = styled(Button)`
  margin: 0 .5rem;
`;

const MyTextField = styled(TextField)`
  margin: 0 0 1rem 0;
  width: 25%;

  @media only screen and (max-width: 645px) {
    width: 100%
  }
`;

function Home(props: MyProps) {
  return (
    <Container>
      <h1 className="m-2">Duocard</h1>
      <MyTextField
        id="outlined-multiline-static"
        label="New Card"
        multiline
        rows={5}
        placeholder="Write your new Word"
        variant="outlined"
      />
      <Row className="w-50">
        <StyledButton variant="contained" color="primary">Save</StyledButton>
        <StyledButton variant="contained" color="secondary">Flip</StyledButton>
      </Row>

      <Card word={"همه کاره"} lang={"Persian"}></Card>
    </Container>
  );
}

export default Home;
