import React from "react";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import TextField from "@material-ui/core/TextField";
import styled from "styled-components";
import Card from "../Card/Card";
import Header from "../Header/Header";
import Misc from "../../hoc/Misc/Misc";

import "./Home.css";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";

type MyProps = {
  message?: string;
};

const StyledButton = styled(Button)`
  margin: 0 .5rem;
`;

const MyTextField = styled(TextField)`
  margin: 0 0 1rem 0;
  width: 25%;

  @media only screen and (max-width: 645px) {
    width: 100%
  }
`;

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
  }),
);

function Home() {
  const classes = useStyles();
  const [age, setAge] = React.useState('');

  const handleChange = (event: React.ChangeEvent<{ value: unknown }>) => {
    setAge(event.target.value as string);
  };

  return (
    <Misc>
      <Header />
      <Container>
        <MyTextField
          id="outlined-multiline-static"
          label="New Card"
          multiline
          rows={5}
          placeholder="Write your new Word"
          variant="outlined"
        />
        <div>
          <StyledButton variant="contained" color="primary">Save</StyledButton>
          <StyledButton variant="contained" color="secondary">Flip</StyledButton>
        </div>

        <Card word={"همه کاره"} lang={"Persian"} type={"adj."}></Card>
        <FormControl variant="outlined" className={classes.formControl}>
          <InputLabel id="demo-simple-select-outlined-label">Age</InputLabel>
          <Select
            labelId="demo-simple-select-outlined-label"
            id="demo-simple-select-outlined"
            value={age}
            onChange={handleChange}
            label="Age"
          >
            <MenuItem value="">
              <em>None</em>
            </MenuItem>
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>
        </FormControl>
      </Container>
    </Misc>
  );
}

export default Home;
