import React, { useState } from "react";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Typography from "@material-ui/core/Typography";
import styled from "styled-components";
import Header from "../Header/Header";
import Misc from "../../hoc/Misc/Misc";
import "./Home.css";
import CardBox from "../NewCard/Box";
import Flex from "../UI/Flex/Flex";
import Grid from "../UI/Grid/Grid";
import Cards from "../Cards/Cards";
import * as WORD_TYPES from "../../store/WordTypes/WordTypes";

const StyledButton = styled(Button)`
  margin: 1rem 0;
  background-color: #43a047;
  width: 10rem;

  :hover {
    background-color: #388e3c;
  }

  @media only screen and (max-width: 768px) {
    width: 100%;
  }
`;

function Home() {
  const [word, setWord] = useState('');
  const [translation, setTranslation] = useState('');
  const [wordLanguage, setWordLanguage] = useState('');
  const [tranLanguage, setTranLanguage] = useState('');
  const [langs, setLangs] = useState([
    {
      id: 0,
      ln: "English"
    },
    {
      id: 1,
      ln: "Spanish"
    },
  ]);
  const [words, setWords] = useState([
    {
      text: "Versatile",
      tran: "همه‌کاره",
      lang: 0,
      pos: WORD_TYPES.NOUN
    }
  ]);

  const handleSetWord = (event: React.ChangeEvent<{ value: unknown }>) => {
    setWord(event.target.value as string);
  };

  const handleSetTranslation = (event: React.ChangeEvent<{ value: unknown }>) => {
    setTranslation(event.target.value as string);
  };

  const handleSetWordLanguage = (event: React.ChangeEvent<{ value: unknown }>) => {
    setWordLanguage(event.target.value as string);
  };

  const handleSetTranLanguage = (event: React.ChangeEvent<{ value: unknown }>) => {
    setTranLanguage(event.target.value as string);
  };

  return (
    <Misc>
      <Header />
      <Container>
        <Flex direction="col">
          <Typography
            variant="h5"
            color="textPrimary"
            gutterBottom
          >
            Add a new Card
          </Typography>
          <br />
          <Grid>
            <CardBox
              input={handleSetWord}
              label="Word"
              placeholder="Write a new word..."
              langHandler={handleSetWordLanguage}
              lang={wordLanguage}
              langs={langs}
              isWord
            />
            <CardBox
              input={handleSetTranslation}
              label="Translation"
              placeholder="Write the translation..."
              langHandler={handleSetTranLanguage}
              lang={tranLanguage}
              langs={langs}
            />
          </Grid>
          <Flex
            direction="row"
            align="center"
            justify="center"
          >
            <StyledButton
              variant="contained"
              color="primary"
              size="large"
            >
              Add
            </StyledButton>
          </Flex>
        </Flex>
      </Container>
      <Cards words={words} />
    </Misc >
  );
}

export default Home;
