import React from "react";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import TextField from "@material-ui/core/TextField";
import Typography from "@material-ui/core/Typography";
import styled from "styled-components";
import Card from "../Card/Card";
import Header from "../Header/Header";
import Misc from "../../hoc/Misc/Misc";

import "./Home.css";

type MyProps = {
  message?: string;
};

const StyledButton = styled(Button)`
  margin: 0 .5rem;
`;

const MyTextField = styled(TextField)`
  margin: 0 0 1rem 0;
  width: 25%;

  @media only screen and (max-width: 645px) {
    width: 100%
  }
`;

function Home() {
  return (
    <Container>
      <Typography variant="h3" component="h2" color="textPrimary" gutterBottom>
        DuocarD
      </Typography>
      <MyTextField
        id="outlined-multiline-static"
        label="New Card"
        multiline
        rows={5}
        placeholder="Write your new Word"
        variant="outlined"
      />
      <div>
        <StyledButton variant="contained" color="primary">Save</StyledButton>
        <StyledButton variant="contained" color="secondary">Flip</StyledButton>
      </div>

      <Card word={"همه کاره"} lang={"Persian"} type={"adj."}></Card>
    </Container>
  );
}

export default Home;
