import React, {useState} from "react";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Typography from "@material-ui/core/Typography";
import styled from "styled-components";
import Header from "../Header/Header";
import Misc from "../../hoc/Misc/Misc";
import "./Home.css";
import CardBox from "../NewCard/Box";
import Flex from "../UI/Flex/Flex";
import Grid from "../UI/Grid/Grid";
import Cards from "../Cards/Cards";
import * as WORD_TYPES from "../../store/WordTypes/WordTypes";

const StyledButton = styled(Button)`
  margin: 1rem 0;
  background-color: #43a047;
  width: 10rem;

  :hover {
    background-color: #388e3c;
  }

  @media only screen and (max-width: 768px) {
    width: 100%;
  }
`;

function Home() {
  const [word, setWord] = useState('');
  const [tran, setTran] = useState(''); // Translation
  const [lang, setLang] = useState(''); // Language
  const [langs, setLangs] = useState([
    {
      id: 0,
      ln: "English"
    },
    {
      id: 1,
      ln: "Spanish"
    },
  ]);
  const [words, setWords] = useState([
    {
      id: 0,
      text: "Versatile",
      tran: "همه‌کاره",
      lang: 0,
      pos: WORD_TYPES.ADJECTIVE
    },
    {
      id: 1,
      text: "Foregoing",
      tran: "قبلی",
      lang: 0,
      pos: WORD_TYPES.NOUN
    },
    {
      id: 2,
      text: "Coincidencia",
      tran: "تصادف",
      lang: 1,
      pos: WORD_TYPES.NOUN
    },
  ]);
  const [pos, setPos] = useState(-1);
  const [errors, setErrors] = useState({
    word: false,
    tran: false,
    lang: false,
    pos: false
  });

  const handleSetWord = (event: React.ChangeEvent<{ value: unknown }>) => {
    setWord(event.target.value as string);
  };

  const handleSetTranslation = (event: React.ChangeEvent<{ value: unknown }>) => {
    setTran(event.target.value as string);
  };

  const handleSetWordLanguage = (event: React.ChangeEvent<{ value: unknown }>) => {
    setLang(event.target.value as string);
  };

  const handleSetPos = (event: React.ChangeEvent<{ value: unknown }>) => {
    const val = parseInt(event.target.value as string);
    setPos(val as number);
  };

  const addWordHandler = () => {
    let flag: boolean = false;
    if (word === "") {
      flag = true;
      setErrors({...errors, word: true});
    }
    if (tran === "") {
      flag = true;
      setErrors({...errors, tran: true});
    }
    if (lang === "") {
      flag = true;
      setErrors({...errors, lang: true});
    }
    if (pos === -1) {
      flag = true;
      setErrors({...errors, pos: true});
    }

    if (flag)
      return;

    let newWords = [...words];
    const id = newWords[newWords.length - 1].id + 1;

    newWords.push({
      id: id,
      text: word,
      tran: tran,
      lang: 5,
      pos: WORD_TYPES.ADJECTIVE
    });
    setWords(newWords)
  }

  return (
    <Misc>
      <Header/>
      <Container>
        <Flex direction="col">
          <Typography
            variant="h5"
            color="textPrimary"
            gutterBottom
          >
            Add a new Card
          </Typography>
          <br/>
          <Grid>
            <CardBox
              input={handleSetWord}
              label="Word"
              placeholder="Write a new word..."
              langHandler={handleSetWordLanguage}
              pos={pos}
              handlePos={handleSetPos}
              lang={lang}
              langs={langs}
              isWord
              error={errors.word}
            />
            <CardBox
              input={handleSetTranslation}
              label="Translation"
              placeholder="Write the translation..."
              error={errors.tran}
            />
          </Grid>
          <Flex
            direction="row"
            align="center"
            justify="center"
          >
            <StyledButton
              variant="contained"
              color="primary"
              size="large"
              onClick={addWordHandler}
            >
              Add
            </StyledButton>
          </Flex>
        </Flex>

        <Cards words={words}/>
      </Container>
    </Misc>
  );
}

export default Home;
