import React, {useState} from 'react';
import {makeStyles} from '@material-ui/core/styles';
import MuiCard from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
  root: {
    minWidth: 275
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginTop: 24,
  },
  flip: {
    margin: "auto",
    textAlign: "center"
  }
});

type Lang = {
  id: number;
  ln: string;
}

type MyProps = {
  text: string;
  tran: string;
  lang: number;
  pos: string;
  langs: Lang[];
}

function Card(props: MyProps) {
  const classes = useStyles();
  const [side, setSide] = useState(true);
  // const lang = props.langs.find(lang => lang.id === props.lang)[0];
  for (let lang of props.langs) {
    console.log(props.lang === lang.id);
  }

  return (
    <MuiCard className={classes.root} variant={"outlined"}>
      <CardContent>
        <Typography className={classes.title} color="textSecondary" gutterBottom>

        </Typography>
        <Typography variant="h5" component="h2">
          {side ? props.text : props.tran}
        </Typography>
        <Typography className={classes.pos} color="textSecondary">
          {props.pos}
        </Typography>
      </CardContent>
      <CardActions>
        <Button
          className={classes.flip}
          variant="outlined"
          color="primary"
          size="small"
          onClick={() => {
            setSide(side => !side)
          }}
        >
          Flip
        </Button>
      </CardActions>
    </MuiCard>
  );
}

export default Card;
