import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import MuiCard from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Input from './Input/Input';
import Languages from './Languages/Languages';
import WordType from './WordType/WordType';

const useStyles = makeStyles({
	root: {
		minWidth: 275,
		boxShadow: "0 2px 8px rgba(156, 156, 156, .5)",
	},
	title: {
		fontSize: 14,
	},
	pos: {
		marginBottom: 4,
	},
	actions: {
		margin: "auto",
		textAlighn: "center",
		display: "flex",
		flexDirection: "row",
		justifyContent: "center"
	}
});

type NewCardProps = {
	label: string;
	placeholder: string;
	isWord?: boolean;
	input?: any;
	language?: string;
	pos?: number;
	langHandler: any;
	lang: string;
}

function NewCard(props: NewCardProps) {
	const classes = useStyles();

	return (
		<MuiCard className={classes.root} variant={"outlined"}>
			<CardContent>
				<Typography variant="h6">{props.label}</Typography>
				<Input
					input={props.input}
					label={props.label}
					placeholder={props.placeholder}
				/>
			</CardContent>
			<CardActions className={classes.actions}>
				<Languages
					langHandler={props.langHandler}
					lang={props.lang}
				/>
				{props.isWord ? <WordType /> : null}
			</CardActions>
		</MuiCard>
	);
}

export default NewCard;
