import React from 'react';
import styled from 'styled-components';
import "./Flex.css";

const Flex = styled.div`
	display: flex;
`;

type FlexProps = {
	children: any;
	direction: "initial" | "inherit" | "row" | "row-reversed" | "col" | "col-reversed";
	align?: "center" | "stretch" | "baseline" | "start" | "end";
	justify?: "center" | "between" | "around" | "start" | "end";
}

const flex = (props: FlexProps) =>
	<Flex className={props.direction + props.align != " " ? "align-" + props.align : " " + props.justify != " " ? "justify-" + props.justify : " "}>
		{props.children}
	</Flex>
	;

export default flex;
