import React, {useState} from "react";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Typography from "@material-ui/core/Typography";
import styled from "styled-components";
import Header from "../Header/Header";
import Misc from "../../hoc/Misc/Misc";
import "./Home.css";
import { makeStyles } from "@material-ui/core/styles";
import CardBox from "../NewCard/Box";
import Flex from "../UI/Flex/Flex";
import Grid from "../UI/Grid/Grid";

const useStyles = makeStyles({
  container: {
    alignItems: "center",
    justifyContent: "center"
  }
});

const StyledButton = styled(Button)`
  margin: 1rem;
  background-color: #43a047;
  width: 10rem;

  :hover {
    background-color: #388e3c;
  }
`;

function Home() {
  const classes = useStyles();
  const [word, setWord] = useState('');
	const handleChange = (event: React.ChangeEvent<{ value: unknown }>) => {
		setWord(event.target.value as string);
	};

  return (
    <Misc>
      <Header />
      <Container>
        <Flex direction="col">
          <Typography variant="h5" color="textPrimary" gutterBottom>
            Add new Card
          </Typography>
          <br />
          <Grid>
            <CardBox label="Word" placeholder="Write a new word..." />
            <CardBox label="Translation" placeholder="Write the translation..." />
          </Grid>
          <Flex direction="row" align="center" justify="center">
            <StyledButton variant="contained" color="primary" size="large">Save</StyledButton>
          </Flex>
        </Flex>
      </Container>
      {/* <Card word={"همه کاره"} lang={"Persian"} type={"adj."}></Card> */}
    </Misc >
  );
}

export default Home;
