import React from "react";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import TextField from "@material-ui/core/TextField";
import styled from "styled-components";
import Card from "../Card/Card";
import Header from "../Header/Header";
import Misc from "../../hoc/Misc/Misc";
import Select from "../Select/Select";
import Input from "../Input/Input";
import "./Home.css";
import { makeStyles } from "@material-ui/core/styles";
import Flex from "../UI/Flex/Flex";

const useStyles = makeStyles({
  container: {
    alignItems: "center",
    justifyContent: "center"
  }
});

const StyledButton = styled(Button)`
  margin: 0 .5rem;
  background-color: #43a047;
  width: 10rem;

  :hover {
    background-color: #388e3c;
  }
`;

const MuiTextField = styled(TextField)`
  margin: 0 0 1rem 0;
  width: 25%;

  @media only screen and (max-width: 645px) {
    width: 100%
  }
`;

function Home() {
  const classes = useStyles();

  return (
    <Misc>
      <Header />
      <Container>
        <Flex direction="col">
          <Flex direction="row" align="center" justify="center">
            <Input label="Word" />
            <Select />

            <Select />
            <Input label="Translation" />
          </Flex>
          <Flex direction="row" align="center" justify="center">
            <StyledButton variant="contained" color="primary">Save</StyledButton>
            {/* <StyledButton variant="contained" color="secondary">Flip</StyledButton> */}
          </Flex>
        </Flex>
      </Container>
      <Card word={"همه کاره"} lang={"Persian"} type={"adj."}></Card>
    </Misc >
  );
}

export default Home;
