import React from 'react';
import Grid from "../UI/Grid/Grid";
import Card from "./Card/Card";
import "./Cards.css";

type Word = {
  id: number;
  text: string;
  tran: string;
  lang: number;
  pos: string;
}

type Lang = {
  id: number;
  ln: string;
}

type MyProps = {
  words: Word[];
  langs: Lang[];
  removeHandler: any;
}

function Cards(props: MyProps) {
  return (
    <Grid col={3}>
      {props.words.map((word: Word, index: number) =>
          <Card
            key={index}
            word={word}
            langs={props.langs}
            removeHandler={props.removeHandler}
          />
      )}
    </Grid>
  );
}

export default Cards;
