  // };

  const handleSetWord = (event: React.ChangeEvent<{ value: unknown }>) => {
      setWord(event.target.value as string);
    };

  return (
    <Misc>
      <Header />
      <Container>
        <Flex direction="col">
          <Typography variant="h5" color="textPrimary" gutterBottom>
            Add new Card
          </Typography>
          <br />
          <Grid>
            <CardBox input={setWord} label="Word" placeholder="Write a new word..." isWord />
            <CardBox input={setTranslation} label="Translation" placeholder="Write the translation..." />
          </Grid>
          <Flex direction="row" align="center" justify="center">
            <StyledButton variant="contained" color="primary" size="large">Save</StyledButton>
          </Flex>
        </Flex>
      </Container>
      {/* <Card word={"همه کاره"} lang={"Persian"} type={"adj."}></Card> */}
    </Misc >
  );
}

export default Home;
