import React, { useState } from 'react';
import MuiCard from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Grow from '@material-ui/core/Grow';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';

const useStyles = makeStyles((theme: Theme) => createStyles({
  root: {
    minWidth: 275,
    boxShadow: "0 2px 6px rgba(127, 127, 127, .25)"
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginTop: 24,
  },
  flip: {
    margin: "auto",
    textAlign: "center"
  },
  text: {
    transition: `all .5s ${theme.transitions.easing.easeInOut}`,
    animation: `$fade .5s ${theme.transitions.easing.easeInOut}`,
  },
  "@keyframes fade": {
    "0%": {
      opacity: 0,
      transform: "translateY(-.25rem)"
    },
    "100%": {
      opacity: 1,
      transform: "translateY(0)"
    }
  },
}));

type Word = {
  id: number;
  text: string;
  tran: string;
  lang: number;
  pos: string;
}

type Lang = {
  id: number;
  ln: string;
}

type MyProps = {
  word: Word;
  langs: Lang[];
  removeHandler: any;
}

function Card(props: MyProps) {
  const classes = useStyles();
  const [side, setSide] = useState(true);
  let lang: string | null = null;
  for (let l of props.langs) {
    if (props.word.lang === l.id) {
      lang = l.ln;
      break;
    }
  }

  return (
    <Grow
      in={true}
      style={{ transformOrigin: '0 0 -16' }}
      {...(true ? { timeout: 800 } : {})}
    >
      <MuiCard className={classes.root} variant={"outlined"}>
        <CardContent>
          <Typography className={classes.title} color="textSecondary" gutterBottom>
            {lang}
          </Typography>
          <Transition
        in={props.showSave}
        timeout={200}
        mountOnEnter
        unmountOnExit
      >
        {state => {
          return (
            <Typography variant="h5" component="h2" className={classes.text}>
              {props.word.text}
            </Typography>
          )
        }}
      </Transition>
          {side ?
            <Typography variant="h5" component="h2" className={classes.text}>
              {props.word.text}
            </Typography>
            :
            <Typography variant="h5" component="h2" className={classes.text}>
              {props.word.tran}
            </Typography>
          }
          {/* <Typography variant="h5" component="h2" className={classes.text}>
            {side ? props.word.text : props.word.tran}
          </Typography> */}
          <Typography className={classes.pos} color="textSecondary">
            {props.word.pos}
          </Typography>
        </CardContent>
        <CardActions>
          <Button
            className={classes.flip}
            variant="outlined"
            color="primary"
            size="small"
            onClick={() => {
              setSide(side => !side)
            }}
          >
            Flip
          </Button>

          <Button
            className={classes.flip}
            variant="outlined"
            color="secondary"
            size="small"
            onClick={() => {
              props.removeHandler(props.word.id)
            }}
          >
            Delete
          </Button>
        </CardActions>
      </MuiCard>
    </Grow>
  );
}

export default Card;
