import React, { useState } from 'react';
import MuiCard from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Grow from '@material-ui/core/Grow';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';

const useStyles = makeStyles((theme: Theme) => createStyles({
  root: {
    minWidth: 275,
    boxShadow: "0 2px 6px rgba(127, 127, 127, .25)",
    transformStyle: "preserve-3d",
    position: "relative",
    width: "100%",
    height: "100%",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginTop: 24,
  },
  flip: {
    margin: "auto",
    textAlign: "center"
  },
  text: {
    transition: `all .5s ${theme.transitions.easing.easeInOut}`,
  },
  rot: {
    transform: "rotateX(180deg)"
  },
  front: {
    position: "absolute",
    // width: "100%",
    // height: "100%",
    backfaceVisibility: "hidden",
  },
  back: {
    position: "absolute",
    // width: "100%",
    // height: "100%",
    backfaceVisibility: "hidden",
  }
}));

type Word = {
  id: number;
  text: string;
  tran: string;
  lang: number;
  pos: string;
}

type Lang = {
  id: number;
  ln: string;
}

type MyProps = {
  word: Word;
  langs: Lang[];
  removeHandler: any;
}

function Card(props: MyProps) {
  const classes = useStyles();
  const [side, setSide] = useState(true);
  let lang: string | null = null;
  for (let l of props.langs) {
    if (props.word.lang === l.id) {
      lang = l.ln;
      break;
    }
  }

  return (
    <Grow
      in={true}
      style={{ transformOrigin: '0 0 -16', transform: side ? "" : "rotateX(180deg)" }}
      {...(true ? { timeout: 800 } : {})}
    >
      <div>
        <MuiCard className={classes.root} variant={"outlined"} className={classes.front}>
          <CardContent>
            <Typography className={classes.title} color="textSecondary" gutterBottom>
              {lang}
            </Typography>
            <Typography
              variant="h5"
              component="h2"
            >
              {props.word.text}
            </Typography>
            <Typography className={classes.pos} color="textSecondary">
              {props.word.pos}
            </Typography>
          </CardContent>
          <CardActions>
            <Button
              className={classes.flip}
              variant="outlined"
              color="primary"
              size="small"
              onClick={() => {
                setSide(side => !side)
              }}
            >
              Flip
            </Button>

            <Button
              className={classes.flip}
              variant="outlined"
              color="secondary"
              size="small"
              onClick={() => {
                props.removeHandler(props.word.id)
              }}
            >
              Delete
            </Button>
          </CardActions>
        </MuiCard>

        <MuiCard className={classes.root} variant={"outlined"} className={classes.back}>
          <CardContent>
            <Typography className={classes.title} color="textSecondary" gutterBottom>
              {lang}
            </Typography>
            <Typography
              variant="h5"
              component="h2"
              className={classes.text}
            >
              {props.word.tran}
            </Typography>
            <Typography className={classes.pos} color="textSecondary">
              {props.word.pos}
            </Typography>
          </CardContent>
          <CardActions>
            <Button
              className={classes.flip}
              variant="outlined"
              color="primary"
              size="small"
              onClick={() => {
                setSide(side => !side)
              }}
            >
              Flip
            </Button>

            <Button
              className={classes.flip}
              variant="outlined"
              color="secondary"
              size="small"
              onClick={() => {
                props.removeHandler(props.word.id)
              }}
            >
              Delete
            </Button>
          </CardActions>
        </MuiCard>
      </div>
    </Grow >
  );
}

export default Card;
